long long int gcd1 ( long long int k , long long int l ) { long long int a , b ; if ( k > l ) { a = k ; b = l ; } else { a = l ; b = k ; } long long int c , r ; if ( ( a / b ) == 0 ) { c = a % b ; } else { c = a / b ; } r = a - ( c * b ) ; if ( r == 0 ) return b ; else gcd1 ( b , r ) ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int a , b , c , d ; scanf ( "%lld%lld" , & a , & b ) ; c = gcd1 ( a , b ) ; d = ( a * b ) / c ; printf ( "%lld %lld\n" , c , d ) ; } return 0 ; }