int gcd ( int a , int b ) { if ( a == b ) return a ; else if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( ) { int t , n1 , n2 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & n1 , & n2 ) ; printf ( "%d %d\n" , lcm ( n1 , n2 ) , gcd ( n1 , n2 ) ) ; } return 0 ; }