long long int gcd ( long long int a , long long int b ) { long long int x ; if ( a >= b ) { while ( a % b != 0 ) { x = a ; a = b ; b = x % a ; } return b ; } else return gcd ( b , a ) ; } long long int lcm ( long long int a , long long int b ) { long long int x , y ; x = gcd ( a , b ) ; y = ( a / x ) * ( b / x ) * x ; return y ; } long long int main ( ) { long long int t ; scanf ( "%lld" , & t ) ; while ( t -- ) { long long int a , b , x , y ; scanf ( "%lld %lld" , & a , & b ) ; x = gcd ( a , b ) ; y = lcm ( a , b ) ; printf ( "%lld %lld\n" , x , y ) ; } return 0 ; }