int main ( ) { int n , i , d , a , b , j , k ; scanf ( "%d" , & n ) ; for ( k = 0 ; k < n ; k ++ ) { d = 0 ; scanf ( "%d%d" , & a , & b ) ; if ( b > a > 0 ) { for ( i = a ; i > 0 ; ) { if ( a % i == 0 && b % i == 0 ) { printf ( "%d " , i ) ; d = ( a * b ) / i ; printf ( "%d\n" , d ) ; i = 0 ; } i -- ; } } else if ( a > b > 0 ) { for ( j = b ; j > 0 ; ) { if ( b % j == 0 && a % j == 0 ) { printf ( "%d " , j ) ; d = ( a * b ) / j ; printf ( "%d\n" , d ) ; j = 0 ; } j -- ; } } } return 0 ; }