int main ( ) { int n , r , i , j , k , e ; scanf ( "%d" , & n ) ; int ans [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & r ) ; int a [ r ] [ r ] , f [ r ] [ r ] ; for ( j = 0 ; j < r ; j ++ ) { for ( k = 0 ; k < r ; k ++ ) { a [ j ] [ k ] = 0 ; } } for ( j = 0 ; j < r ; j ++ ) { for ( k = 0 ; k < j ; k ++ ) { scanf ( "%d" , & a [ j ] [ k ] ) ; } } f [ 0 ] [ 0 ] = a [ 0 ] [ 0 ] ; for ( j = 1 ; j < r ; j ++ ) { for ( k = 0 ; k < j ; k ++ ) { if ( k == 0 ) f [ j ] [ k ] = f [ j - 1 ] [ k ] + a [ j ] [ k ] ; else if ( j != k ) { if ( f [ j - 1 ] [ k - 1 ] > f [ j - 1 ] [ k ] ) f [ j ] [ k ] = f [ j - 1 ] [ k - 1 ] + a [ j ] [ k ] ; else f [ j ] [ k ] = f [ j - 1 ] [ k ] + a [ j ] [ k ] ; } else f [ j ] [ k ] = f [ j - 1 ] [ k - 1 ] + a [ j ] [ k ] ; } } int max = f [ r - 1 ] [ 0 ] ; for ( e = 1 ; e < r ; e ++ ) { if ( f [ r - 1 ] [ e ] > max ) max = f [ r - 1 ] [ e ] ; } ans [ i ] = max ; } for ( i = 0 ; i < n ; i ++ ) { printf ( "%d\n" , ans [ i ] ) ; } return 0 ; }