main ( ) { int t , i ; scanf ( "%d" , & t ) ; while ( t > 0 ) { int a , b ; int gcd ; int high , low ; scanf ( "%d %d" , & a , & b ) ; if ( a < b ) { high = b ; low = a ; } else { low = b ; high = a ; } for ( i = 1 ; i <= low ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { gcd = i ; } } int lcm = 1 , p = 1 ; for ( i = low ; i > 0 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { p = i * p ; a = a / i ; b = b / i ; } } p = p * a * b ; printf ( "%d %d\n" , gcd , p ) ; lcm = 1 ; t -- ; } return 0 ; }