int main ( void ) { short n , t , a , q , e , k , c , d , sum = 0 ; scanf ( "%hd" , & t ) ; for ( a = 1 ; a <= t ; a ++ ) { scanf ( "%hd" , & e ) ; sum = 0 ; k = 0 ; for ( c = 1 ; c <= e ; c ++ ) { scanf ( "%hd" , & n ) ; for ( d = 1 ; d <= c - 1 ; d ++ ) { scanf ( "%hd" , & q ) ; if ( q > n ) { k = q ; n = q ; } else if ( n > q ) k = n ; else k = q ; } sum += k ; } printf ( "%hd\n" , sum ) ; } return 0 ; }