int main ( ) { int i , x , n , m , j ; scanf ( "%d" , & x ) ; while ( x != 0 ) { scanf ( "\n%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( j = 0 ; j < n - 1 ; j ++ ) { if ( a [ j ] < a [ j + 1 ] ) { int t = a [ j ] ; a [ j ] = a [ j + 1 ] ; a [ j + 1 ] = t ; } } m = a [ n - 1 ] * ( n - 1 ) ; printf ( "\n%d" , m ) ; x -- ; } return 0 ; }