int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int a , b , num , den , r , gcd , lcm ; scanf ( "%ld%ld" , & a , & b ) ; if ( a > b ) { num = a ; den = b ; } else { num = b ; den = a ; } r = num % den ; while ( r != 0 ) { num = den ; den = r ; r = num % den ; } gcd = den ; lcm = ( a * b ) / gcd ; printf ( "%ld " , gcd ) ; printf ( "%ld\n" , lcm ) ; } return 0 ; }