int main ( ) { int array [ MAX ] [ MAX ] ; int i , x , n , j , k , max1 = 0 , max2 = 0 , temp , iter , max_set ; i = 0 ; scanf ( "%d" , & n ) ; while ( i < n ) { scanf ( "%d" , & x ) ; for ( j = 0 ; j < 100 ; j ++ ) { for ( k = 0 ; k < 100 ; k ++ ) { array [ j ] [ k ] = 0 ; } } for ( j = 0 ; j < x ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & array [ j ] [ k ] ) ; } } iter = 0 ; max_set = 0 ; temp = 0 ; for ( k = 0 ; k < x ; k ++ ) { temp = 0 ; for ( j = iter ; j < x - 1 ; j ++ ) { temp = temp + array [ j ] [ k ] ; } max1 = temp + array [ x - 1 ] [ k ] ; if ( ( k + 1 ) < x ) { max2 = temp + array [ x - 1 ] [ k + 1 ] ; } else max2 = 0 ; if ( max1 > max_set ) { max_set = max1 ; } if ( max2 > max_set ) { max_set = max2 ; } iter ++ ; } printf ( "%d\n" , max_set ) ; i ++ ; } return 0 ; }