int main ( ) { long long int i , j , n , k , m , t ; long long int gcd , lcm , max , min ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld %lld" , & m , & n ) ; min = m > n ? n : m ; max = m > n ? m : n ; for ( j = 1 ; j <= min ; j ++ ) { if ( m % j == 0 && n % j == 0 ) { gcd = j ; } } for ( k = min ; ; k ++ ) { if ( k % m == 0 && k % n == 0 ) { lcm = k ; break ; } } printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }