int main ( ) { int a [ 100 ] ; int t , i , j , k , n , count ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; ++ i ) { count = 0 ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; ++ j ) { for ( k = 0 ; k <= j ; ++ k ) { scanf ( "%d" , & a [ k ] ) ; if ( j != 0 ) { if ( k != 0 ) { if ( a [ k ] < a [ k - 1 ] ) a [ k ] = a [ k - 1 ] ; } } } if ( j != 0 ) count = count + a [ k - 1 ] ; } printf ( "%d\n" , count ) ; } return 0 ; }