int main ( ) { int a , i , n , b , g , l , t , c ; scanf ( "%d" , & t ) ; for ( c = 1 ; c <= t ; c ++ ) { scanf ( "%d %d" , & a , & b ) ; g = a ; i = b ; while ( a != b ) { if ( a > b ) { a = a - b ; } else { b = b - a ; } } printf ( "%d " , a ) ; n = ( g > i ) ? g : i ; while ( 1 ) { if ( n % g == 0 && n % i == 0 ) { l = n ; printf ( "%d\n" , l ) ; break ; } n ++ ; } } return 0 ; }