int main ( ) { int T , a , b , m , i ; int gcd , lcm ; scanf ( "%d" , & T ) ; while ( T -- ) { scanf ( "%d%d" , & a , & b ) ; m = ( a > b ? b : a ) ; gcd = 1 ; for ( i = m ; i > 0 ; i -- ) { if ( ( a % i == 0 ) && ( b % i == 0 ) ) { gcd = gcd * i ; a /= i ; b /= i ; } else { lcm = gcd * a * b ; } } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }