int gcd ( int a , int b ) ; int lcm ( int c , int d ) ; int main ( ) { int n1 , n2 , t , i , s1 , s2 ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & n1 , & n2 ) ; s1 = gcd ( n1 , n2 ) ; s2 = lcm ( n1 , n2 ) ; printf ( "%d\t%d" , s1 , s2 ) ; } return 0 ; } int gcd ( int a , int b ) { int rem ; rem = a % b ; if ( rem == 0 ) return b ; else return ( gcd ( b , rem ) ) ; } int lcm ( int c , int d ) { int rem ; rem = c % d ; if ( rem != 0 ) return ( c * d ) ; else return c ; }