int main ( int argc , char const * argv [ ] ) { int tests ; scanf ( "%d" , & tests ) ; while ( tests -- ) { int A , B , lcm ; scanf ( "%d %d" , & A , & B ) ; int n1 = A , n2 = B ; if ( A > B ) A = A ^ B , B = A ^ B , A = A ^ B ; while ( B % A ) { B = B % A ; A = A ^ B ; B = A ^ B ; A = A ^ B ; } printf ( "\n" ) ; lcm = ( n1 * n2 ) / A ; printf ( "%d %d\n" , A , lcm ) ; } return 0 ; }