int main ( ) { int t , n , i , sum ; scanf ( "%d" , & t ) ; while ( t -- > 0 ) { scanf ( "%d" , & n ) ; int a [ n ] ; sum = 0 ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { sum = sum + a [ i + 1 ] ; } else { sum = sum + a [ i ] ; a [ i + 1 ] = a [ i ] ; } } printf ( "%d\n" , sum ) ; } return 0 ; }