int main ( ) { int t , i , a , b , c , d , e , f , rem , lcm , gcd , num , den ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { num = a ; den = b ; } else { num = b ; den = a ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = a * b / gcd ; printf ( "%d%d" , gcd , lcm ) ; scanf ( "%d%d" , & c , & d ) ; if ( c > d ) { num = c ; den = d ; } else { num = d ; den = c ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = c * d / gcd ; printf ( "%d%d" , gcd , lcm ) ; scanf ( "%d%d" , & e , & f ) ; if ( e > f ) { num = e ; den = f ; } else { num = f ; den = e ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = c * d / gcd ; printf ( "%d%d" , gcd , lcm ) ; return 0 ; }