int main ( ) { int n1 , n2 , k , i , minMultiple , gcd ; printf ( "Enter two positive integers: " ) ; scanf ( "%d %d" , & n1 , & n2 ) ; minMultiple = ( n1 > n2 ) ? n1 : n2 ; while ( 1 ) { if ( minMultiple % n1 == 0 && minMultiple % n2 == 0 ) { k = minMultiple ; break ; } ++ minMultiple ; } printf ( "%d" , k ) ; for ( i = 1 ; i <= n1 && i <= n2 ; ++ i ) { if ( n1 % i == 0 && n2 % i == 0 ) gcd = i ; } printf ( "%d" , gcd ) ; printf ( "\n" ) ; return 0 ; }