int t ; int main ( ) { scanf ( "%d" , & t ) ; while ( t -- ) { long a , b , c , i , d , e , f = 0 , g , j = 0 ; scanf ( "%ld" , & a ) ; scanf ( "%ld" , & b ) ; if ( a > b ) { c = b ; b = a ; a = c ; } for ( i = 1 ; i <= a ; i ++ ) { d = a % i ; e = b % i ; if ( d == 0 && e == 0 ) { f = i ; } } printf ( "%ld" , f ) ; for ( j = ( a * b ) ; j >= b ; j -- ) { d = j % a ; e = j % b ; if ( d == 0 && e == 0 ) { g = j ; } } printf ( " %ld\n" , g ) ; } }