long int gcd ( long int a , long int b ) { if ( a == 0 || b == 0 ) return 0 ; else if ( a == b ) return a ; else if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } long int lcm ( long int a , long int b ) { return ( ( a * b ) / gcd ( a , b ) ) ; } int main ( ) { int t , i ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { long int a , b ; scanf ( "%ld %ld" , & a , & b ) ; printf ( "%ld %ld\n" , gcd ( a , b ) , lcm ( a , b ) ) ; } }