int main ( ) { int T , N , i , min , j , arr1 [ 15 ] , arr2 [ 100 ] , arr3 [ 50000 ] ; scanf ( "%d" , & T ) ; if ( T >= 1 && T <= 10 ) { for ( i = 1 ; i <= T ; i ++ ) { scanf ( "%d" , & N ) ; if ( N >= 2 && N <= 15 ) { scanf ( "%d " , & arr1 [ 0 ] ) ; min = arr1 [ 0 ] ; for ( j = 1 ; j < N ; j ++ ) { scanf ( "%d" , & arr1 [ j ] ) ; if ( arr1 [ j ] < min ) min = arr1 [ j ] ; } } else if ( N >= 2 && N <= 100 ) { scanf ( "%d " , & arr2 [ 0 ] ) ; min = arr2 [ 0 ] ; for ( j = 1 ; j < N ; j ++ ) { scanf ( "%d" , & arr2 [ j ] ) ; if ( arr2 [ j ] < min ) min = arr2 [ j ] ; } } else if ( N >= 2 && N <= 50000 ) { scanf ( "%d " , & arr3 [ 0 ] ) ; min = arr3 [ 0 ] ; for ( j = 1 ; j < N ; j ++ ) { scanf ( "%d" , & arr3 [ j ] ) ; if ( arr3 [ j ] < min ) min = arr3 [ j ] ; } } printf ( "%d\n" , min * ( N - 1 ) ) ; } } return 0 ; }