int main ( ) { char n [ 10 ] , row [ 200 ] ; int no_of_rows = 0 , i = 0 , largest = 0 , k = 0 , sum = 0 , t = 0 ; scanf ( "%d" , & t ) ; fflush ( stdin ) ; while ( t -- ) { fgets ( n , 10 , stdin ) ; sscanf ( n , "%d" , & no_of_rows ) ; while ( no_of_rows -- ) { fgets ( row , 200 , stdin ) ; largest = 0 ; if ( strlen ( row ) == 2 ) continue ; for ( i = 0 ; i <= strlen ( row ) - 2 ; i ++ ) { if ( row [ i ] != ' ' || row [ i ] != '\0' ) { k = row [ i ] - '0' ; if ( k > largest ) largest = k ; } } sum += largest ; } printf ( "%d\n" , sum ) ; } return 0 ; }