int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n , i , j , count = 0 , k ; scanf ( "%lld" , & n ) ; long long int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & arr [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { k = arr [ i ] ; for ( j = i ; j < n ; j ++ ) { if ( arr [ j ] >= k ) { count ++ ; k = arr [ j ] ; } else { k = 99999999999999999 ; break ; } } } printf ( "%lld\n" , count ) ; } }