main ( ) { long long int i , k , j , n , t , s , temp , min ; scanf ( "%lld" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%lld" , & s ) ; int a [ s ] ; for ( j = 0 ; j < s ; j ++ ) { scanf ( "%lld" , & a [ j ] ) ; if ( a [ j ] < 1 ) { break ; } } min = a [ 0 ] ; for ( k = 1 ; k < s ; k ++ ) { if ( min > a [ k ] ) { temp = min ; min = a [ k ] ; a [ k ] = temp ; } } printf ( "%lld" , min * ( s - 1 ) ) ; } }