int main ( void ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { unsigned int a , b ; scanf ( "%u %u" , & a , & b ) ; int c = a ; if ( b > a ) c = b ; int f = 1 ; for ( int i = c ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { f = i ; break ; } } printf ( "%u" , f ) ; c = a ; if ( b < a ) c = b ; f = 1 ; for ( int i = c ; i <= ( a * b ) ; i ++ ) { if ( i % a == 0 && i % b == 0 ) { f = i ; break ; } } printf ( " %u\n" , f ) ; } }