int main ( ) { long long int T , N , * arr , i , j , k , ctr , count , * counts , ptr ; scanf ( "%lld" , & T ) ; counts = malloc ( sizeof ( long long int ) * T ) ; for ( i = 0 ; i < T ; i ++ ) { ctr = 1 , count = 0 ; scanf ( "%lld" , & N ) ; arr = malloc ( sizeof ( long long int ) * N ) ; for ( j = 0 ; j < N ; j ++ ) scanf ( "%lld" , & arr [ j ] ) ; printf ( "entered everything\n" ) ; ptr = 0 ; while ( ptr < N ) { ctr = 1 ; j = ptr ; while ( j < N - 1 ) if ( arr [ j ] < arr [ j + 1 ] ) { j ++ ; ctr ++ ; } else break ; for ( k = ctr ; k >= 1 ; k -- ) count = count + k ; ptr = j + 1 ; } counts [ i ] = count ; } for ( i = 0 ; i < T ; i ++ ) printf ( "%lld\n" , counts [ i ] ) ; return 0 ; }