int main ( ) { int t , n , i , j , k , a , b ; printf ( "Enter t val:" ) ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { printf ( "Enter n val:" ) ; scanf ( "%d" , & n ) ; int arr [ n ] [ n ] ; for ( j = 0 ; j < n ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & arr [ j ] [ k ] ) ; } } for ( j = n - 2 ; j >= 0 ; j -- ) { for ( k = 0 ; k < n - 1 ; k ++ ) { a = arr [ j ] [ k ] + arr [ j + 1 ] [ k ] ; b = arr [ j ] [ k ] + arr [ j + 1 ] [ k + 1 ] ; arr [ j ] [ k ] = a > b ? a : b ; } } printf ( "\n%d" , arr [ 0 ] [ 0 ] ) ; } return 0 ; }