int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int aa [ n ] , i , total = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & aa [ i ] ) ; } while ( n > 1 ) { if ( aa [ n - 2 ] > aa [ n - 1 ] ) { total += aa [ n - 1 ] ; aa [ n - 2 ] = aa [ n - 1 ] ; n -- ; } else { total += aa [ n - 2 ] ; n -- ; } } printf ( "%d\n" , total ) ; } return 0 ; }