int calculate_gcd ( int a , int b ) { int rem = a % b ; if ( rem == 0 ) return b ; else calculate_gcd ( b , rem ) ; } int main ( ) { int long i , lcm , gcd , t , a , b ; unsigned long long prod = 0 ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; prod = a * b ; gcd = calculate_gcd ( a , b ) ; lcm = prod / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; lcm = 0 ; } }