int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int main ( ) { int a , b , t , result1 = 0 , result2 = 0 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; result1 = gcd ( a , b ) ; result2 = ( a * b ) / result1 ; printf ( "%d %d" , result1 , result2 ) ; } return 0 ; }