int main ( ) { int t , k ; scanf ( "%d" , & t ) ; for ( k = 0 ; k < t ; k ++ ) { long long int a , b , i , j = b , gcd = 0 , lcm = 0 ; scanf ( "%lld %lld" , & a , & b ) ; if ( b >= a ) { for ( i = a ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) gcd = i ; } for ( j = b ; ; j ++ ) { if ( j % a == 0 && j % b == 0 ) { lcm = j ; break ; } } } else { for ( i = b ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) gcd = i ; } for ( j = a ; ; j ++ ) { if ( j % a == 0 && j % b == 0 ) { lcm = j ; break ; } } } printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }