int main ( ) { int t , i , x , y , a , b , j ; long int lcm , hcf ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { hcf = 1 ; scanf ( "%d %d" , & x , & y ) ; a = x ; b = y ; if ( a > b ) { for ( j = 2 ; j <= b ; j ++ ) { if ( ( a % j == 0 ) && ( b % j == 0 ) ) { while ( ( a % j == 0 ) && ( b % j == 0 ) ) { a = a / j ; b = b / j ; hcf = hcf * j ; } } } } else { for ( j = 2 ; j <= a ; j ++ ) { if ( ( a % j == 0 ) && ( b % j == 0 ) ) { while ( ( a % j == 0 ) && ( b % j == 0 ) ) { a = a / j ; b = b / j ; hcf = hcf * j ; } } } } lcm = x * y / hcf ; printf ( "%ld %ld\n" , hcf , lcm ) ; } return 0 ; }