int main ( ) { int t , j ; scanf ( "%d" , & t ) ; for ( j = 0 ; j < t ; j ++ ) { long int a , b ; scanf ( "%ld%ld" , & a , & b ) ; long int temp , rem , n , gcd , lcm ; if ( a <= b ) { n = b ; rem = a ; } else { n = a ; rem = b ; } while ( n % rem != 0 ) { temp = rem ; rem = n % rem ; n = temp ; } gcd = rem ; lcm = ( a * b ) / gcd ; printf ( "%ld %ld" , gcd , lcm ) ; } return 0 ; }