long int gcd ( long int a , long int b ) { long int c ; if ( a < b ) { c = a ; a = b ; b = c ; } if ( b == 0 ) return a ; else return gcd ( b , a % b ) ; } int main ( ) { long int T ; long int i ; long int x ; long int y ; long int A , B ; scanf ( "%ld" , & T ) ; for ( i = 1 ; i <= T ; i ++ ) { scanf ( "%ld%ld" , & A , & B ) ; x = gcd ( A , B ) ; y = ( A * B ) / x ; printf ( "%ld %ld\n" , x , y ) ; } return 0 ; }