int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , i , gcd = 0 , lcm = 1 ; scanf ( "%d%d" , & a , & b ) ; for ( i = b ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { gcd = i ; break ; } } for ( i = 2 ; i <= b ; i ++ ) { while ( a % i == 0 && b % i == 0 ) { a = a / i ; b = b / i ; lcm = lcm * i ; } } lcm = lcm * a * b ; printf ( "\n%d  %d" , gcd , lcm ) ; } return 0 ; }