int main ( void ) { int t , a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; int nmrt , dmrt , rem , gcd , lcm ; if ( a > b ) { nmrt = a ; dmrt = b ; } else { nmrt = b ; dmrt = a ; } rem = nmrt % dmrt ; while ( rem != 0 ) { nmrt = dmrt ; dmrt = rem ; rem = nmrt % dmrt ; } gcd = dmrt ; lcm = a * b / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }