int main ( ) { int i , j , n , l , temp1 = 0 , temp2 = 0 ; scanf ( "%d\n" , & n ) ; while ( n -- ) { scanf ( "%d\n" , & l ) ; int a [ l ] [ l ] ; for ( i = 0 ; i < l ; i ++ ) { for ( j = 0 ; j < l ; j ++ ) { scanf ( "%d\n" , & a [ i ] [ j ] ) ; } } int sum = a [ 0 ] [ 0 ] ; for ( i = 0 ; i < l ; i ++ ) { for ( j = 0 ; j < l ; j ++ ) { temp1 = a [ i + 1 ] [ j ] ; temp2 = a [ i + 1 ] [ j + 1 ] ; if ( temp1 >= temp2 ) { sum += temp1 ; } else { sum += temp2 ; } } } printf ( "%d\n" , sum ) ; } return 0 ; }