int main ( ) { int t ; scanf ( "%d " , & t ) ; while ( t > 0 ) { int a , i , j , m = 0 , n = 0 ; scanf ( "%d " , & a ) ; int b [ a ] [ a ] ; for ( i = 0 ; i < a ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & b [ i ] [ j ] ) ; } } for ( j = 0 ; j < a ; j ++ ) { if ( j == a - 1 && b [ j ] [ j ] > m ) { n = n - 2 * m + b [ j ] [ j ] ; } else m = b [ j ] [ j ] ; for ( i = j ; i < a - 1 ; i ++ ) { if ( m < b [ i ] [ j ] ) m = b [ i ] [ j ] ; } n = n + m ; } printf ( "%d\n" , n ) ; t -- ; } return 0 ; }