long long int lcm ( long long int a , long long int b , long long int * hcf_add ) { long long int x ; x = a * b ; while ( a != b ) { if ( a > b ) a = a - b ; else b = b - a ; } * hcf_add = a ; return ( x / a ) ; } void main ( ) { int t ; long long int num1 , num2 , lcm_val , hcf ; scanf ( "%d" , & t ) ; while ( t -- > 0 ) { scanf ( "%lld%lld" , & num1 , & num2 ) ; lcm_val = lcm ( num1 , num2 , & hcf ) ; printf ( "%lld %lld\n" , hcf , lcm_val ) ; } }