int main ( void ) { int i , t ; scanf ( "%d\n" , & t ) ; while ( t -- ) { long long int a , b , gcd = 0 , lcm = 0 ; scanf ( "%lld %lld" , & a , & b ) ; for ( i = 1 ; i <= a && i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { gcd = i ; } } lcm = ( a * b ) / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }