int gcd ( int , int ) ; int lcm ( int , int , int ) ; int main ( ) { int num1 , num2 , x , y , t ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & num1 , & num2 ) ; x = gcd ( num1 , num2 ) ; y = lcm ( num1 , num2 , x ) ; printf ( "%d %d\n" , x , y ) ; } return 0 ; } int gcd ( int a , int b ) { if ( a == 0 ) return b ; else if ( b == 0 ) return a ; else if ( a == 0 && b == 0 ) return 0 ; else if ( a != 0 && b != 0 ) { if ( a >= b ) gcd ( a % b , b ) ; else gcd ( a , b % a ) ; } else ; } int lcm ( int a , int b , int c ) { int p ; p = ( a * b ) / c ; return p ; }