int main ( ) { int n , i , t , prev , k = 0 , b [ 100 ] ; long sum ; scanf ( "%d" , & t ) ; while ( t != 0 ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; sum = 0 ; prev = 0 ; i = 0 ; while ( prev < n ) { i = prev ; if ( a [ i ] > a [ i + 1 ] ) { sum += a [ i + 1 ] ; prev = i + 1 ; } else { sum += a [ i ] ; a [ i + 1 ] = a [ i ] ; prev = i + 1 ; } } b [ k ++ ] = sum ; t -- ; } for ( i = 0 ; i < t ; i ++ ) printf ( "%d\n" , b [ i ] ) ; return 0 ; }