int row , a [ 100 ] [ 100 ] ; int l ( int n , int m ) { int trsum , tlsum ; if ( row == n ) { return 0 ; } else { tlsum = l ( n + 1 , m ) ; trsum = l ( n + 1 , m + 1 ) ; if ( tlsum > trsum ) { return tlsum + a [ n ] [ m ] ; } else return trsum + a [ n ] [ m ] ; } } int main ( ) { int t , i , m , n , sum ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d" , & row ) ; for ( n = 0 ; n < row ; n ++ ) { for ( m = 0 ; m <= n ; m ++ ) { scanf ( "%d" , ( * ( a + n ) + m ) ) ; } } sum = l ( 0 , 0 ) ; printf ( "%d\n" , sum ) ; } return 0 ; }