main ( ) { int n , i ; scanf ( "%d" , & n ) ; int temp [ n ] ; for ( i = 0 ; i < n ; i ++ ) { int no , j , k ; scanf ( "%d" , & no ) ; int a [ no ] [ no ] ; int sum [ no ] [ no ] ; for ( k = 0 ; k < no ; i ++ ) { for ( j = 0 ; j < no ; j ++ ) a [ k ] [ j ] = 0 ; } k = j = 0 ; for ( k = 0 ; k < no ; k ++ ) { for ( j = 0 ; j <= k ; j ++ ) scanf ( "%d" , & a [ k ] [ j ] ) ; sum [ i ] [ k ] = sum [ i ] [ k ] + a [ k ] [ j ] ; } temp [ i ] = sum [ i ] [ 0 ] ; for ( j = 0 ; j < no ; j ++ ) { if ( temp [ i ] <= sum [ i ] [ j ] ) temp [ i ] = sum [ i ] [ j ] ; } } for ( i = 0 ; i < n ; i ++ ) printf ( "%d\n" , temp [ i ] ) ; return 0 ; }