int ans [ 100 ] [ 100 ] ; int trgl [ 100 ] [ 100 ] ; int max ( int a , int b ) { if ( a > b ) return a ; return b ; } int maxsum ( int tot ) { int x , col ; for ( x = tot - 1 ; x >= 0 ; x -- ) { if ( x == tot - 1 ) { for ( col = 0 ; col < tot ; col ++ ) ans [ x ] [ col ] = trgl [ x ] [ col ] ; } else { for ( col = 0 ; col <= x ; col ++ ) ans [ x ] [ col ] = trgl [ x ] [ col ] + max ( ans [ x + 1 ] [ col ] , ans [ x + 1 ] [ col + 1 ] ) ; } } return ans [ 0 ] [ 0 ] ; } void triangle ( int number ) { int row , col ; for ( col = 0 ; col < number ; col ++ ) { for ( row = 0 ; row <= col ; row ++ ) { scanf ( "%d" , & trgl [ col ] [ row ] ) ; printf ( "" ) ; } } printf ( "%d" , maxsum ( number ) ) ; } void do_it ( ) { int number ; scanf ( "%d" , & number ) ; triangle ( number ) ; } int main ( ) { int x , n ; scanf ( "%d" , & x ) ; for ( n = 0 ; n < x ; n ++ ) do_it ( ) ; return 0 ; }