int main ( ) { int t , n , cost , i , j ; scanf ( "%d" , & t ) ; while ( t -- ) { cost = 0 ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & arr [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( arr [ i ] > arr [ i + 1 ] ) { cost += arr [ i + 1 ] ; for ( j = i + 1 ; j < n - 1 ; j ++ ) arr [ j - 1 ] = arr [ j ] ; } else { cost += arr [ i ] ; for ( j = i + 1 ; j < n - 1 ; j ++ ) arr [ j ] = arr [ j + 1 ] ; } } printf ( "%d\n" , cost ) ; } return 0 ; }