int main ( ) { long long int i , t , j , gcd , lcm , a , b ; scanf ( "%lld" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; if ( a > b ) { for ( j = b ; j >= 1 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { gcd = j ; break ; } } } else { for ( j = a ; j >= 1 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { gcd = j ; break ; } } } lcm = ( a * b ) / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }