int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int n ; scanf ( "%ld" , & n ) ; long int a [ n ] ; int i , p ; p = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld" , & a [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { p = p + a [ i + 1 ] ; a [ i ] = a [ n - 1 ] ; n = n - 1 ; } else if ( a [ i + 1 ] > a [ i ] ) { p = p + a [ i ] ; a [ i + 1 ] = a [ n - 1 ] ; n = n - 1 ; } } printf ( "%d\n" , p ) ; } return 0 ; }