int main ( ) { long int t , i , n , min , max , count ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld" , & n ) ; count = 0 ; long int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld" , & arr [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { count += arr [ i ] ; } if ( n == 2 ) { if ( arr [ 0 ] > arr [ 1 ] ) { printf ( "%ld \n" , arr [ 1 ] ) ; } else printf ( "%ld \n" , arr [ 0 ] ) ; } else { min = arr [ 0 ] ; max = arr [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { if ( min > arr [ i ] ) { min = arr [ i ] ; } if ( max < arr [ i ] ) { max = arr [ i ] ; } } printf ( "%ld \n" , count - min - max ) ; } } return 0 ; }